        <div class="comment-empty">
                <div class="warp">
                <i class="w-icon-normal icon-normal-comment-empty"></i>
                <p class="text">评论还是空滴</p>
                </div>
                </div>
    <% } %>
<% } %>
        </ul>
    </script>
    <script id="j-commentTpl" type="text/x-jqote-template">
<% var levelNameList = ["普通用户", "小白生活家", "智慧生活家", "品质生活家", "至尊生活家"] %>
        <div class="m-commentUser">
        <div class="avatarWarp">
        <img src="<%= this . frontUserAvatar || 'http://yanxuan.nosdn.127.net/485ff1be6912be6ae696b6d360d1b101.png' %>" alt="<%= this . frontUserName %>"/>
        <div class="mask w-icon-normal icon-normal-mask"></div>
        </div>
<% if (this . memberLevel && (this . memberLevel > 0) && (this . memberLevel < 5)) { %>
        <div class="username-withIcon">
            <a class="w-icon-member member-comment-vip<%= this . memberLevel %>" href="/membership/index" title="<%= levelNameList[this . memberLevel] %>" ></a><div class="username f-center"><%= this . frontUserName %></div>
            </div>
<% } else { %>
        <div class="username f-center"><%= this . frontUserName %></div>
<% } %>
        </div>
        <div class="m-comment f-clearfix">
<% if (!!this . content > 0) { %>
        <div class="content"><%= this . content %></div>
<% } %>
<% if (this . picList && this . picList . length > 0) { %>
    <%=
    $('#j-picListTpl').jqote( {
        picList: this.picList,
        target: this.target
        })
        %>
    <% } %>
        <div class="meta">
            <div class="skuInfo">
    <%
    for(var k = 0, len = this . skuInfo . length;
    k < len;
    k++) {
        %>
        <span class="mr20"><%= this . skuInfo[k] %></span>
        <%
    }
    %>
        </div>
            <div class="right">
            <div class="createTime"><%= this . createTime %></div>
            </div>
            </div>
            <div id="<%= this . target %>" class="lightbox"></div>
    <% if (this . commentReplyVO != null && this . commentReplyVO . replyContent != '') { %>
        <div class="reply f-clearfix">
                <div class="serviceName f-left">【小选回复】：</div>
                <div class="content"><%= this.commentReplyVO.replyContent.replace(/\n/g, '<br/>') %></div>
                </div>
    <% } %>
        </div>
        </script>
        <script id="j-picListTpl" type="text/x-jqote-template">
            <ul class="j-picList m-picList picList" data-target="<%= this . target %>">
    <% for(var i = 0;
    i < this . picList . length;
    ++i) { %>
        <li class="item">
                <div class="before"></div>
                <img src="<%= this . picList[i] %>"/>
                </li>
    <% } %>
        </ul>
        </script>
        <script id="topic-list" type="text/x-jqote-template">
    <%
    for(var i = 0;
    i < this . topicList . length;
    i++){
    var topic = this . topicList[i];
    %>
        <a href="/topic/<%= topic . topicId %>?_stat_area=right_<%= (i + 1) %>&_stat_referer=item" class="item" target="_blank">
            <li class="item">
            <a href="/topic/<%= topic . topicId %>?_stat_area=right_<%= (i + 1) %>&_stat_referer=item" class="m-topicRecommended" target="_blank">
            <img width="260" height="140" src="<%= topic . scenePicUrl %>?imageView&thumbnail=260y140&quality=100" alt="">
            <div class="text"><%= topic . title %></div>
            </a>
            </li>
<% } %>
    </script>
    <script id="remindPop" type="text/x-jqote-template">
        <div class="desp">输入手机号，该商品到货将第一时间短信提醒您</div>
        <input class="mobile" type="text" placeholder="请输入接收通知的手机号" id="receiveMobile">
        <div class="tips j-tips"></div>
        <a class="btn w-button w-button-primary w-button-xl j-submit" href="javascript:void(0);">
        <span>
        确定
        </span>
        </a>
    </script>
    <script id="template-reportList" type="text/x-jqote-template">
        <div class="m-report-modal j-report-modal">
        <div class="cover"></div>
        <div class="modal">
        <div class="modal-body">
        <div class="close"></div>
        <div class="content j-report-slide">
<% for(var i = 0;
i<this.list.length;
i++) { %>
        <div class="slide"><img src="<%= this.list[i] . src %>" alt="" height="<%= this.list[i] . h %>" width="<%= this.list[i] . w %>"></div>
<% } %>
        </div>
        </div>
        </div>
        </div>
    </script>
    <script id="template-recommend" type="text/x-jqote-template">
<% if (this . data . items && this . data . items . length > 0) { %>
        <div class="bd">
            <div class="m-recommend">
            <header>
            <h3>大家都在看</h3>
            </header>
            <div class="recommend-wrap">
            <div class="recommend-content">
            <ul class="m-recommendItemList" id="j-recommendItemList">
            </ul>
            </div>
            <div class="m-recommend-btn btn-left"></div>
            <div class="m-recommend-btn btn-right"></div>
            </div>
            </div>
            </div>
<% } %>
    </script>
</div>
@endsection